effect.script:
let sortedControls;
sortedControls = this.newControls.sort((a, b) => (a.effectiveness > b.effectiveness) ? 1 : -1);
sortedControls = sortedControls.sort((a, b) => (a.cost < b.cost) ? 1 : -1);
console.debug(this.newControls);
const recControl = sortedControls[0];
alert(`You should get ${recControl.number}. ${recControl.name} next!`);
